mapper.select:
SELECT N_XH, N_CLLBXH, C_SXRY, N_CLXSZLC, D_SQRQ, C_SHJG FROM (SELECT S.*, ROWNUM RN FROM (SELECT N_XH, FUN_GET_CPHM(N_CLLBXH) N_CLLBXH, C_SXRY, N_CLXSZLC, TO_CHAR(D_SQRQ, 'YYYY-MM-DD') D_SQRQ, (CASE WHEN C_SFSH = '0' THEN '待审核' WHEN C_SFSH = '1' AND C_SHJG = '01' THEN '等待警保部门审核' WHEN C_SFSH = '1' AND C_SHJG = '11' THEN '审核通过' WHEN C_SFSH = '1' AND C_SHJG = '00' THEN '审核不通过' END) C_SHJG FROM B_CLWX WHERE 1 = 1
AND C_CLBM = #{c_clbm}
AND N_CLLBXH = #{n_cllbxh}
AND C_SFSH = '0'
AND C_SFSH = '1' AND C_SHJG = '01'
ORDER BY N_XH DESC) S WHERE ROWNUM <= #{limit}) T WHERE T.RN > #{start}

SELECT COUNT(*) Total FROM B_CLWX WHERE 1 = 1
AND C_CLBM = #{c_clbm}
AND N_CLLBXH = #{n_cllbxh}
AND C_SFSH = '0'
AND C_SFSH = '1' AND C_SHJG = '01'
