mapper.select:
SELECT N_XH, N_CLLBXH, C_SXRY, N_CLXSZLC, D_SQRQ FROM (SELECT S.*, ROWNUM RN FROM (SELECT N_XH, FUN_GET_CPHM(N_CLLBXH) N_CLLBXH, C_SXRY, N_CLXSZLC, TO_CHAR(D_SQRQ,'YYYY-MM-DD') D_SQRQ FROM B_CLWX
WHERE N_CLLBXH = #{n_cllbxh}
ORDER BY N_XH DESC) S WHERE ROWNUM <= #{limit}) T WHERE T.RN > #{start}

SELECT COUNT(*) Total
  FROM B_CLWX
  
	WHERE N_CLLBXH = #{n_cllbxh}
