mapper.select:
		 select 
			base.N_XH,
			base.C_ZDYMC,
			base.N_CDXH,
			base.USER_ID,
			base.N_XSXH,
			base.C_YXX
		 from B_WDZM base 
  
	  and N_XH = #{nXh}
	  and C_ZDYMC = #{cZdymc}
	  and N_CDXH = #{nCdxh}
	  and USER_ID = #{userId}
	  and N_XSXH = #{nXsxh}
	  and C_YXX = #{cYxx}

ORDER BY N_XH  ASC


		 select 
			base.N_XH,
			base.C_ZDYMC,
			base.N_CDXH,
			base.USER_ID,
			base.N_XSXH,
			base.C_YXX
		 from B_WDZM base 
  
	  and N_XH = #{nXh}
	  and C_ZDYMC = #{cZdymc}
	  and N_CDXH = #{nCdxh}
	  and USER_ID = #{userId}
	  and N_XSXH = #{nXsxh}
	  and C_YXX = #{cYxx}

ORDER BY N_XH  ASC

				select count(base.N_XH) from B_WDZM base
 		 
			  and N_XH = #{nXh}
			  and C_ZDYMC = #{cZdymc}
			  and N_CDXH = #{nCdxh}
			  and USER_ID = #{userId}
			  and N_XSXH = #{nXsxh}
			  and C_YXX = #{cYxx}

mapper.insert:
	INSERT INTO B_WDZM(
		 N_XH
	  ,  C_ZDYMC
	  ,  N_CDXH
	  ,  USER_ID
	  ,  N_XSXH
	  , C_YXX
	)VALUES(
		SEQ_WDZM.NEXTVAL 
		
	  , #{cZdymc} 
	  ,  #{nCdxh} 
	  ,  #{userId} 
	  ,  #{nXsxh} 
	  ,  #{cYxx} 
	)

	 
	 SELECT  SEQ_WDZM.CURRVAL  AS ID FROM dual

mapper.update:
 
			update B_WDZM 
			 
				 , C_ZDYMC = #{cZdymc}
				 , N_CDXH = #{nCdxh}
				 , USER_ID = #{userId}
				 , N_XSXH = #{nXsxh}
				 , C_YXX = #{cYxx}
			
			  
				  and N_XH = #{nXh}
