mapper.select:
select base.N_XH, base.C_SQDW, base.C_NSYHYS, to_char(base.D_HYRQ,'YYYY-MM-DD') D_KSSJ, to_char(base.D_KSSJ,'YYYY-MM-DD HH24:mi:ss') D_KSSJ, to_char(base.D_JSSJ,'YYYY-MM-DD HH24:mi:ss') D_JSSJ, base.C_HYNR, base.C_ZCR, base.C_LXR, base.C_LXDH, base.C_SHR, to_char(base.D_SHRQ,'YYYY-MM-DD') D_SHRQ, base.C_SHZT from B_KQ_HYSSQ base
and N_XH = #{nXh} and C_SQDW = #{cSqdw} and C_NSYHYS = #{cNsyhys}
and D_HYRQ >= to_date(#{dHyrqBeg},'YYYY-MM-DD') and D_HYRQ <= to_date(#{dHyrqEnd},'YYYY-MM-DD')
and D_KSSJ >= to_date(#{dKssjBeg},'YYYY-MM-DD') and D_KSSJ <= to_date(#{dKssjEnd},'YYYY-MM-DD')
and D_JSSJ >= to_date(#{dJssjBeg},'YYYY-MM-DD') and D_JSSJ <= to_date(#{dJssjEnd},'YYYY-MM-DD')
and C_HYNR = #{cHynr} and C_ZCR = #{cZcr} and C_LXR = #{cLxr} and C_LXDH = #{cLxdh} and C_SHR = #{cShr}
and D_SHRQ >= to_date(#{dShrqBeg},'YYYY-MM-DD') and D_SHRQ <= to_date(#{dShrqEnd},'YYYY-MM-DD')
and C_SHZT = #{cShzt}
ORDER BY base.N_XH DESC

select base.N_XH, base.C_SQDW, base.C_NSYHYS, to_char(base.D_HYRQ,'YYYY-MM-DD') D_KSSJ, to_char(base.D_KSSJ,'YYYY-MM-DD HH24:mi:ss') D_KSSJ, to_char(base.D_JSSJ,'YYYY-MM-DD HH24:mi:ss') D_JSSJ, base.C_HYNR, base.C_ZCR, base.C_LXR, base.C_LXDH, base.C_SHR, to_char(base.D_SHRQ,'YYYY-MM-DD') D_SHRQ, base.C_SHZT from B_KQ_HYSSQ base
and N_XH = #{nXh} and C_SQDW = #{cSqdw} and C_NSYHYS = #{cNsyhys}
and D_HYRQ >= to_date(#{dHyrqBeg},'YYYY-MM-DD') and D_HYRQ <= to_date(#{dHyrqEnd},'YYYY-MM-DD')
and D_KSSJ >= to_date(#{dKssjBeg},'YYYY-MM-DD') and D_KSSJ <= to_date(#{dKssjEnd},'YYYY-MM-DD')
and D_JSSJ >= to_date(#{dJssjBeg},'YYYY-MM-DD') and D_JSSJ <= to_date(#{dJssjEnd},'YYYY-MM-DD')
and C_HYNR = #{cHynr} and C_ZCR = #{cZcr} and C_LXR = #{cLxr} and C_LXDH = #{cLxdh} and C_SHR = #{cShr}
and D_SHRQ >= to_date(#{dShrqBeg},'YYYY-MM-DD') and D_SHRQ <= to_date(#{dShrqEnd},'YYYY-MM-DD')
and C_SHZT = #{cShzt}
ORDER BY base.N_XH DESC

		select count(base.N_XH) from B_KQ_HYSSQ base
   
		  and N_XH = #{nXh}
	  and C_SQDW = #{cSqdw}
	  and C_NSYHYS = #{cNsyhys}
	
	
	  and D_HYRQ >= to_date(#{dHyrqBeg},'YYYY-MM-DD')
	 and D_HYRQ <= to_date(#{dHyrqEnd},'YYYY-MM-DD')
	
	
	  and D_KSSJ >= to_date(#{dKssjBeg},'YYYY-MM-DD')
	 and D_KSSJ <= to_date(#{dKssjEnd},'YYYY-MM-DD')
	
	  and D_JSSJ >= to_date(#{dJssjBeg},'YYYY-MM-DD')
	 and D_JSSJ <= to_date(#{dJssjEnd},'YYYY-MM-DD')
	
	
	  and C_HYNR = #{cHynr}
	  and C_ZCR = #{cZcr}
	  and C_LXR = #{cLxr}
	  and C_LXDH = #{cLxdh}
	  and C_SHR = #{cShr}
	
	
	  and D_SHRQ >= to_date(#{dShrqBeg},'YYYY-MM-DD')
	 and D_SHRQ <= to_date(#{dShrqEnd},'YYYY-MM-DD')
	
	  and C_SHZT = #{cShzt}

ORDER BY base.N_XH  DESC

