mapper.select:
		 select 
			base.N_XH,
			base.C_HYMC,
			to_char(base.D_KSSJ,'YYYY-MM-DD HH24:mi:ss')  D_KSSJ,
			base.C_HYSC,
			base."C_HYDD",
			base.C_CHFW,
			base.C_FC
		 from B_KQ_HYTS  base 
  
	  and N_XH = #{nXh}
	  and C_HYMC = #{cHymc}
	
	  and D_KSSJ >= to_date(#{dKssjBeg},'YYYY-MM-DD')
	 and D_KSSJ <= to_date(#{dKssjEnd},'YYYY-MM-DD')
	
	  and C_HYSC = #{cHysc}
	  and C_HYDD = #{cHydd}
	
	
	  and C_CHFW = #{cChfw}
	  and C_FC = #{cFc}

ORDER BY base.N_XH  DESC


