	  ,  to_date(#{dKssj},'YYYY-MM-DD HH24:mi:ss')
	  , #{cHysc}
	  ,  #{cHydd}
	  ,  #{cChfw}
	  ,   #{cFc}
	)

	 
	 SELECT  SEQ_KQ_HYTS.CURRVAL  AS ID FROM dual

