mapper.select:
select base.N_XH, base.USER_ID, to_char(base.D_RQ,'YYYY-MM-DD') D_RQ, base.C_SJ, base.C_TXYY, to_char(base.D_DJ,'YYYY-MM-DD') D_DJ, base.C_ZT from B_KQ_QQSB base
and N_XH = #{nXh} and USER_ID = #{userId}
and D_RQ >= to_date(#{dRqBeg},'YYYY-MM-DD') and D_RQ < to_date(#{dRqEnd},'YYYY-MM-DD')
and C_SJ = #{cSj} and C_TXYY = #{cYy}
and D_DJ >= to_date(#{dDjBeg},'YYYY-MM-DD') and D_DJ < to_date(#{dDjEnd},'YYYY-MM-DD')
and C_ZT = #{cZt}
ORDER BY base.N_XH DESC

select base.N_XH, base.USER_ID, to_char(base.D_RQ,'YYYY-MM-DD') D_RQ, base.C_SJ, base.C_TXYY, to_char(base.D_DJ,'YYYY-MM-DD') D_DJ, base.C_ZT from B_KQ_QQSB base
and N_XH = #{nXh} and USER_ID = #{userId}
and D_RQ >= to_date(#{dRqBeg},'YYYY-MM-DD') and D_RQ < to_date(#{dRqEnd},'YYYY-MM-DD')
and C_SJ = #{cSj} and C_TXYY = #{cYy}
and D_DJ >= to_date(#{dDjBeg},'YYYY-MM-DD') and D_DJ < to_date(#{dDjEnd},'YYYY-MM-DD')
and C_ZT = #{cZt}
ORDER BY base.N_XH DESC

		select count(base.N_XH) from  B_KQ_QQSB base
	  
	  and N_XH = #{nXh}
	  and USER_ID = #{userId}
	
	  and D_RQ >= to_date(#{dRqBeg},'YYYY-MM-DD')
	 and D_RQ < to_date(#{dRqEnd},'YYYY-MM-DD')
	
	  and C_SJ = #{cSj}
	  and C_TXYY = #{cYy}
	
	  and D_DJ >= to_date(#{dDjBeg},'YYYY-MM-DD')
	 and D_DJ < to_date(#{dDjEnd},'YYYY-MM-DD')
	
	  and C_ZT = #{cZt}

ORDER BY base.N_XH  DESC

