mapper.select:
		select count(base.N_XH) from B_KQ_SXB base
	  
	  and N_XH = #{nXh}
	  and USER_ID = #{userId}
	  and N_LX = #{nLx}
	  and D_DJ >= to_date(#{dDjBeg},'YYYY-MM-DD')
	 and D_DJ < to_date(#{dDjEnd},'YYYY-MM-DD')
	
	  and C_ZT = #{cZt}

ORDER BY base.N_XH  DESC

mapper.insert:
	INSERT INTO B_KQ_SXB(
		 N_XH
	  , USER_ID
	  ,  N_LX
	  ,  D_DJ
	  ,  C_ZT
	)VALUES(
		SEQ_KQ_SXB.NEXTVAL 
		
	  ,  #{userId}
	  ,  #{nLx}
	  ,  to_date(#{dDj},'YYYY-MM-DD')
	  ,  #{cZt}
	)

	 
	 SELECT  SEQ_KQ_SXB.CURRVAL  AS ID FROM dual

mapper.update:
 
			update B_KQ_SXB 
			 
			, USER_ID =  #{userId}
			, N_LX =  #{nLx}
			, D_DJ = to_date(#{dDj},'YYYY-MM-DD')
			, C_ZT =   #{cZt}
			
			  
				  and N_XH = #{nXh}
				,= null and nXhs !='' ">  and  N_XH in (${nXhs})

 
			update B_KQ_SXB set 
				 USER_ID =  #{userId}
			, N_LX =  #{nLx}
			, D_DJ =  to_date(#{dDj},'YYYY-MM-DD')
			, C_ZT =   #{cZt}
			WHERE 
				N_XH = #{nXh}
