mapper.select:
select base.N_XH, base.USER_ID, base.N_JB, base.N_YHZ, base.C_ZC, base.C_XM, base.N_XB, to_char(base.D_SR,'YYYY-MM-DD') D_SR , base.C_GJ, base.C_CSD, base.C_HYZK, base.C_XL, base.C_BYYX, base.C_ZY, base.C_KH, base.C_HKSZD, base.C_DHHM, base.C_SJHM, base.C_YX, base.C_DZ, to_char(base.D_GZSJ,'YYYY-MM-DD') D_GZSJ, to_char(base.D_RZSJ,'YYYY-MM-DD') D_RZSJ, base.C_JCJL, base.C_BZ, base.N_DLCS, N_ZXSC, base.N_LX, base.C_YXIP from B_YH base
and N_XH = #{nXh} and USER_ID = #{userId} and N_YHZ = #{nYhz} and C_XM = #{cXm} and D_SR >= to_date(#{dSrBeg},'YYYY-MM-DD') and D_SR < to_date(#{dSrEnd},'YYYY-MM-DD') and N_LX = #{nlx} and C_YXIP = #{cYxip}
ORDER BY base.N_XH DESC

select base.N_XH, base.USER_ID, base.N_JB, base.N_YHZ, base.C_ZC, base.C_XM, base.N_XB, to_char(base.D_SR,'YYYY-MM-DD') D_SR , base.C_GJ, base.C_CSD, base.C_HYZK, base.C_XL, base.C_BYYX, base.C_ZY, base.C_KH, base.C_HKSZD, base.C_DHHM, base.C_SJHM, base.C_YX, base.C_DZ, to_char(base.D_GZSJ,'YYYY-MM-DD') D_GZSJ, to_char(base.D_RZSJ,'YYYY-MM-DD') D_RZSJ, base.C_JCJL, base.C_BZ, base.N_DLCS, N_ZXSC, base.N_LX, base.C_YXIP from B_YH base
and N_XH = #{nXh} and USER_ID = #{userId} and N_YHZ = #{nYhz} and C_XM = #{cXm} and D_SR >= to_date(#{dSrBeg},'YYYY-MM-DD') and D_SR < to_date(#{dSrEnd},'YYYY-MM-DD') and N_LX = #{nlx} and C_YXIP = #{cYxip}
ORDER BY base.N_XH DESC

		select count(base.N_XH) from B_YH base
  
	  and N_XH = #{nXh}
	  and USER_ID = #{userId}
	  and N_YHZ = #{nYhz}
	  and C_XM = #{cXm}
	  and D_SR >= to_date(#{dSrBeg},'YYYY-MM-DD')
	 and D_SR < to_date(#{dSrEnd},'YYYY-MM-DD')
	  and N_LX = #{nlx}
	  and C_YXIP = #{cYxip}

ORDER BY base.N_XH  DESC

