mapper.select:
select base.N_XH, base.USER_ID, base.C_DD, base.N_LX, base.C_YY, to_char(base.D_KSSJ,'YYYY-MM-DD') D_KSSJ, to_char(base.D_JSSJ,'YYYY-MM-DD') D_JSSJ, to_char(base.D_DJ,'YYYY-MM-DD') D_DJ, base.C_ZT, case base.C_ZT when '1' then '是' else '否' end as C_ZT_NAME, FUN_GET_YHMC(USER_ID) USER_NAME, C_SHZT, C_SHNR, C_SHBM, C_SHJB, C_SFLJ, N_QJLX, N_QXJ, C_SHR, to_char(base.D_SHRQ,'YYYY-MM-DD') D_SHRQ, to_char(base.D_XJRQ,'YYYY-MM-DD') D_XJRQ from B_KQ_YBJL base
and N_XH = #{nXh} and USER_ID = #{userId} and N_LX = #{nLx} and C_YY = #{cYy}
and D_KSSJ >= to_date(#{dKssjBeg},'YYYY-MM-DD') and D_KSSJ <= to_date(#{dKssjEnd},'YYYY-MM-DD')
and D_JSSJ >= to_date(#{dJssjBeg},'YYYY-MM-DD') and D_JSSJ <= to_date(#{dJssjEnd},'YYYY-MM-DD')
and D_DJ >= to_date(#{dDjBeg},'YYYY-MM-DD') and D_DJ <= to_date(#{dDjEnd},'YYYY-MM-DD')
and C_ZT = #{cZt}
and C_SHZT = #{cShzt} and C_SHNR = #{cShnr} and C_SHBM = #{cShbm} and C_SHJB = #{cShjb} and C_SFLJ = #{cSflj}
and N_QJLX = #{nQjlx} and N_QXJ = #{nQxj} and C_SHR = #{cShr}
and D_SHRQ >= to_date(#{dShrqBeg},'YYYY-MM-DD') and D_SHRQ <= to_date(#{dShrqEnd},'YYYY-MM-DD')
and D_XJRQ >= to_date(#{dXjrqBeg},'YYYY-MM-DD') and D_XJRQ <= to_date(#{dXjrqEnd},'YYYY-MM-DD')
ORDER BY base.N_XH DESC

select base.N_XH, base.USER_ID, base.C_DD, base.N_LX, base.C_YY, to_char(base.D_KSSJ,'YYYY-MM-DD') D_KSSJ, to_char(base.D_JSSJ,'YYYY-MM-DD') D_JSSJ, to_char(base.D_DJ,'YYYY-MM-DD') D_DJ, base.C_ZT, case base.C_ZT when '1' then '是' else '否' end as C_ZT_NAME, FUN_GET_YHMC(USER_ID) USER_NAME, C_SHZT, C_SHNR, C_SHBM, C_SHJB, C_SFLJ, N_QJLX, N_QXJ, C_SHR, to_char(base.D_SHRQ,'YYYY-MM-DD') D_SHRQ, to_char(base.D_XJRQ,'YYYY-MM-DD') D_XJRQ from B_KQ_YBJL base
and N_XH = #{nXh} and USER_ID = #{userId} and N_LX = #{nLx} and C_YY = #{cYy}
and D_KSSJ >= to_date(#{dKssjBeg},'YYYY-MM-DD') and D_KSSJ <= to_date(#{dKssjEnd},'YYYY-MM-DD')
and D_JSSJ >= to_date(#{dJssjBeg},'YYYY-MM-DD') and D_JSSJ <= to_date(#{dJssjEnd},'YYYY-MM-DD')
and D_DJ >= to_date(#{dDjBeg},'YYYY-MM-DD') and D_DJ <= to_date(#{dDjEnd},'YYYY-MM-DD')
and C_ZT = #{cZt}
and C_SHZT = #{cShzt} and C_SHNR = #{cShnr} and C_SHBM = #{cShbm} and C_SHJB = #{cShjb} and C_SFLJ = #{cSflj}
and N_QJLX = #{nQjlx} and N_QXJ = #{nQxj} and C_SHR = #{cShr}
and D_SHRQ >= to_date(#{dShrqBeg},'YYYY-MM-DD') and D_SHRQ <= to_date(#{dShrqEnd},'YYYY-MM-DD')
and D_XJRQ >= to_date(#{dXjrqBeg},'YYYY-MM-DD') and D_XJRQ <= to_date(#{dXjrqEnd},'YYYY-MM-DD')
ORDER BY base.N_XH DESC

