mapper.select:
select base.N_XH, base.USER_ID, base.C_DD, base.C_YY, to_char(base.D_SJ,'YYYY-MM-DD') D_SJ, base.C_BZ, FUN_GET_YHMC(USER_ID) USER_NAME from B_KQ_ZB base
and N_XH = #{nXh} and USER_ID = #{userId}
and D_SJ >= to_date(#{dSjBeg},'YYYY-MM-DD') and D_SJ < to_date(#{dSjEnd},'YYYY-MM-DD')
and C_BZ = #{cBz}
ORDER BY base.N_XH DESC

select base.N_XH, base.USER_ID, base.C_DD, base.C_YY, to_char(base.D_SJ,'YYYY-MM-DD') D_SJ, base.C_BZ, FUN_GET_YHMC(USER_ID) USER_NAME from B_KQ_ZB base
and N_XH = #{nXh} and USER_ID = #{userId}
and D_SJ >= to_date(#{dSjBeg},'YYYY-MM-DD') and D_SJ < to_date(#{dSjEnd},'YYYY-MM-DD')
and C_BZ = #{cBz}
ORDER BY base.N_XH DESC

		select count(base.N_XH) from B_KQ_ZB base
   
	  and N_XH = #{nXh}
	  and USER_ID = #{userId}
	
	  and D_SJ >= to_date(#{dSjBeg},'YYYY-MM-DD')
	 and D_SJ < to_date(#{dSjEnd},'YYYY-MM-DD')
	
	  and C_BZ = #{cBz}

ORDER BY base.N_XH  DESC

