mapper.insert:
BEGIN INSERT INTO B_NBYJ( N_XH , C_BT , C_YHID , C_YHZID , C_NR , C_IP , C_SFSC , C_ZT , D_DJ ) VALUES ( seq_b_nbyj.nextval, #{c_bt,jdbcType=VARCHAR}, #{c_yhid,jdbcType=VARCHAR}, #{c_yhzid,jdbcType=VARCHAR}, #{c_nr,javaType=String,jdbcType=CLOB,handler=ClobTypeHandler}, #{c_ip,jdbcType=VARCHAR}, #{c_sfsc,jdbcType=VARCHAR}, #{c_zt,jdbcType=VARCHAR}, sysdate ); BEGIN
INSERT INTO B_NBYJ_RECIEVE (N_XH,N_NBYJXH,C_JSR,C_LX,C_ZT) VALUES ( seq_b_nbyj_recieve.nextval, seq_b_nbyj.currval, #{item.c_yhid},#{item.c_lx},'1' )
; END; END;

BEGIN
INSERT INTO B_NBYJ_RECIEVE (N_XH,N_NBYJXH,C_JSR,C_LX,C_ZT) VALUES ( seq_b_nbyj_recieve.nextval, seq_b_nbyj.currval, #{item.c_yhid},#{item.c_lx},'1' )
; END;

mapper.update:
BEGIN UPDATE B_NBYJ SET C_BT = #{c_bt,jdbcType=VARCHAR}, C_NR = #{c_nr,jdbcType=CLOB,handler=ClobTypeHandler}, C_JSR = #{c_jsr,jdbcType=VARCHAR}, C_CSR = #{c_csr,jdbcType=VARCHAR}, C_MSR = #{c_msr,jdbcType=VARCHAR}, C_ZT = #{c_zt,jdbcType=VARCHAR} WHERE N_XH = #{n_xh}; BEGIN
INSERT INTO B_NBYJ_RECIEVE (N_XH,N_NBYJXH,C_JSR,C_LX,C_ZT) VALUES ( seq_b_nbyj_recieve.nextval, #{n_xh}, #{item.c_yhid},#{item.c_lx},'1' )
; END; END;

mapper.select:
SELECT FUN_GET_YHMC(C_JSR) C_YHID, DECODE(C_ZT, '1', '未签收', '2', '已签收', '已删除') C_ZT
 FROM B_NBYJ_RECIEVE
WHERE N_NBYJXH = #{n_xh}

mapper.update:
UPDATE B_NBYJ_RECIEVE
	SET C_ZT = #{c_zt}
WHERE N_NBYJXH = #{n_xh}
 	AND C_JSR = #{c_yhid}
