mapper.select:
select base.N_XH, to_char(D_RQ,'YYYY-MM-DD') as D_RQ, base.C_ZT from B_JIARI base
and N_XH = #{nXh} and D_RQ >= to_date(#{dRqBeg},'YYYY-MM-DD') and D_RQ < to_date(#{dRqEnd},'YYYY-MM-DD') and C_ZT = #{cZt}
ORDER BY base.D_RQ DESC

select base.N_XH, to_char(D_RQ,'YYYY-MM-DD') as D_RQ, base.C_ZT from B_JIARI base
and N_XH = #{nXh} and D_RQ >= to_date(#{dRqBeg},'YYYY-MM-DD') and D_RQ < to_date(#{dRqEnd},'YYYY-MM-DD') and C_ZT = #{cZt}
ORDER BY base.D_RQ DESC

				select count(base.N_XH) from B_JIARI base
 		  
			  and N_XH = #{nXh}
		  and D_RQ >= to_date(#{dRqBeg},'YYYY-MM-DD')
			 and D_RQ < to_date(#{dRqEnd},'YYYY-MM-DD')
			  and C_ZT = #{cZt}

mapper.insert:
	INSERT INTO B_JIARI(
		 N_XH
	  , D_RQ
	  ,  C_ZT
	)VALUES(
		SEQ_JIARI.NEXTVAL 
		
	  ,  to_date(#{dRq},'YYYY-MM-DD')
	  ,  #{cZt}
	)

	 
	 SELECT  SEQ_JIARI.CURRVAL  AS ID FROM dual

	INSERT INTO B_JIARI(
		N_XH,
		D_RQ,
		C_ZT
	)VALUES(
		 SEQ_JIARI.NEXTVAL,  
		
		to_date(#{dRq},'YYYY-MM-DD'),
		#{cZt}
	)

	  
	 SELECT  SEQ_JIARI.CURRVAL  AS ID FROM dual

mapper.update:
 
			update B_JIARI 
			 
				 , D_RQ = to_date(#{dRq},'YYYY-MM-DD')
				 , C_ZT = #{cZt}
			
			  
				  and N_XH = #{nXh}
				  and  N_XH in (${nXhs})

 
			update B_JIARI set 
				N_XH = #{nXh},
				D_RQ = to_date(#{dRq},'YYYY-MM-DD'),
				C_ZT = #{cZt}
			WHERE 
				N_XH = #{nXh}
