mapper.select:
		select count(base.N_XH) from B_KQ_CCJL base
   
	  and N_XH = #{nXh}
	  and USER_ID = #{userId}
	  and C_DD = #{cDd}
	  and C_YY = #{cYy}
	
	  and D_KSSJ >= to_date(#{dKssjBeg},'YYYY-MM-DD')
	 and D_KSSJ <= to_date(#{dKssjEnd},'YYYY-MM-DD')
	
	  and D_JSSJ >= to_date(#{dJssjBeg},'YYYY-MM-DD')
	 and D_JSSJ <= to_date(#{dJssjEnd},'YYYY-MM-DD')
	
	  and D_DJ >= to_date(#{dDjBeg},'YYYY-MM-DD')
	 and D_DJ <= to_date(#{dDjEnd},'YYYY-MM-DD')
	
	  and C_ZT = #{cZt}

ORDER BY base.N_XH  DESC

mapper.insert:
	INSERT INTO B_KQ_CCJL(
		 N_XH
	  , USER_ID
	  ,  C_DD
	  ,  C_YY
	  ,  D_KSSJ
	  ,  D_JSSJ
	  ,  D_DJ
	  ,  C_ZT
	)VALUES(
		SEQ_KQ_CCJL.NEXTVAL 
		
	  ,  #{userId}
	  ,  #{cDd}
	  ,  #{cYy}
	  ,  to_date(#{dKssj},'YYYY-MM-DD')
	  ,  to_date(#{dJssj},'YYYY-MM-DD')
	  ,  to_date(#{dDj},'YYYY-MM-DD')
	  ,  #{cZt}
	)

	 
	 SELECT  SEQ_KQ_CCJL.CURRVAL  AS ID FROM dual

mapper.update:
 
			update B_KQ_CCJL 
			 
			, USER_ID =  #{userId}
			, C_DD =  #{cDd}
			,C_YY =  #{cYy}
			, D_KSSJ =   to_date(#{dKssj},'YYYY-MM-DD')
			, D_JSSJ =   to_date(#{dJssj},'YYYY-MM-DD')
			, D_DJ = to_date(#{dDj},'YYYY-MM-DD')
			, C_ZT =   #{cZt}
			
			  
				  and N_XH = #{nXh}
				,= null and nXhs !='' ">  and  N_XH in (${nXhs})

 
			update B_KQ_CCJL set 
				 USER_ID =  #{userId}
			, C_DD =  #{cDd}
			,C_YY =  #{cYy}
			, D_KSSJ =  to_date(#{dKssj},'YYYY-MM-DD')
			, D_JSSJ = to_date(#{dJssj},'YYYY-MM-DD')
			, D_DJ =  to_date(#{dDj},'YYYY-MM-DD')
			, C_ZT =   #{cZt}
			WHERE 
				N_XH = #{nXh}
