mapper.select:
SELECT N_XH, C_BT, TO_CHAR(D_FBSJ, 'YYYY-MM-DD HH24:MI:SS') D_FBSJ, C_SFZD, C_SFGL, C_SFTJ, C_SHJG FROM B_XINWEN WHERE 1 = 1
AND C_LM LIKE '%;'||#{c_lm}||';%'
AND C_SFSH = #{c_sfsh}
AND C_BT LIKE '%'||#{c_bt}||'%'
AND C_SFSCSP = #{c_sfscsp}
ORDER BY C_SFZD DESC,D_FBSJ DESC

SELECT N_XH, C_BT, TO_CHAR(D_FBSJ, 'YYYY-MM-DD HH24:MI:SS') D_FBSJ, C_SPLJDZ, C_SPFMLJDZ FROM B_XINWEN WHERE C_LM LIKE '%;'||#{c_lm}||';%'
AND C_SFSH = #{c_sfsh}
AND C_BT LIKE '%'||#{c_bt}||'%'
AND C_SFSCSP = #{c_sfscsp}
ORDER BY C_SFZD DESC,D_FBSJ DESC

SELECT N_XH,C_BT, D_FBSJ, C_SFZD, C_SFGL, C_SFTJ, C_SHJG,C_TPLJDZ FROM (SELECT S.*, ROWNUM RN FROM ( SELECT N_XH, C_BT, TO_CHAR(D_FBSJ, 'YYYY-MM-DD') D_FBSJ, NVL(C_SFZD,'0') C_SFZD, NVL(C_SFGL,'0') C_SFGL, NVL(C_SFTJ,'0') C_SFTJ, C_SHJG, C_TPLJDZ FROM B_XINWEN WHERE 1 = 1
AND C_LM LIKE '%;'||#{c_lm}||';%'
AND C_BT LIKE '%'||#{c_bt}||'%'
AND C_SFSH = #{c_sfsh}
ORDER BY C_SFZD DESC,D_FBSJ DESC) S WHERE ROWNUM <= #{limit}) T WHERE T.RN > #{start}

SELECT N_XH,C_BT,D_FBSJ,C_TPLJDZ,C_LJ FROM ( SELECT N_XH, C_BT, TO_CHAR(D_FBSJ, 'MM-DD') D_FBSJ, C_TPLJDZ, C_LJ FROM B_XINWEN WHERE C_LM LIKE '%;'||#{c_lm}||';%'
AND C_TPLJDZ IS NOT NULL
AND C_SHJG = '1' ORDER BY C_SFZD DESC,D_FBSJ DESC ) WHERE ROWNUM < #{rownum}

SELECT N_XH,C_BT,D_FBSJ,C_TPLJDZ FROM ( SELECT N_XH, C_BT, TO_CHAR(D_FBSJ, 'YYYY-MM-DD') D_FBSJ, C_TPLJDZ FROM B_XINWEN WHERE C_LM LIKE '%;'||#{c_lm}||';%'
AND C_TPLJDZ IS NOT NULL
AND C_SHJG = '1' ORDER BY C_SFZD DESC,D_FBSJ DESC ) WHERE ROWNUM < #{rownum}

mapper.update:
UPDATE B_XINWEN
	SET 
	C_SFZD = #{value} 
	C_SFGL = #{value} 
	C_SFTJ = #{value} 
	
		C_SFSH = 1,
		C_SHJG = #{value},
		C_SHR = #{c_yhid},
		D_SHSJ = SYSDATE
				
    WHERE N_XH = #{n_xh}

mapper.select:
  
	  SELECT C_LMMC
		 FROM B_LANMU
	  WHERE C_LMDM = #{c_lmdm}
