mapper.select:
		 select 
			base.N_XH,
			base.N_BXH,
			base.C_DM,
			base.C_MC,
			base.N_XSXH,
			base.C_TJ,
			base.C_SJDM,
			to_char(base.D_DJ,'YYYY-MM-DD') D_DJ
		 from D_DM base 
  
	  and N_XH = #{nXh}
	  and N_BXH = #{nBxh}
	  and C_DM = #{cDm}
	  and C_MC = #{cMc}
	  and N_XSXH = #{nXsxh}
	  and C_TJ = #{cTj}
	  and C_SJDM = #{cSjdm}

ORDER BY N_XH  DESC


		 select 
			base.N_XH,
			base.N_BXH,
			base.C_DM,
			base.C_MC,
			base.N_XSXH,
			base.C_TJ,
			base.C_SJDM,
			to_char(base.D_DJ,'YYYY-MM-DD') D_DJ
		 from D_DM base 
  
	  and N_XH = #{nXh}
	  and N_BXH = #{nBxh}
	  and C_DM = #{cDm}
	  and C_MC = #{cMc}
	  and N_XSXH = #{nXsxh}
	  and C_TJ = #{cTj}
	  and C_SJDM = #{cSjdm}

ORDER BY N_XH  DESC

				select count(base.N_XH) from D_DM base
 		  
			  and N_XH = #{nXh}
			  and N_BXH = #{nBxh}
			  and C_DM = #{cDm}
			  and C_MC = #{cMc}
			  and N_XSXH = #{nXsxh}
			  and C_TJ = #{cTj}
			  and C_SJDM = #{cSjdm}

mapper.insert:
	INSERT INTO D_DM(
		 N_XH
	  , N_BXH
	  ,  C_DM
	  ,  C_MC
	  ,  N_XSXH
	  , C_TJ
	  , C_SJDM
	  , D_DJ
	)VALUES(
		SEQ_DM.NEXTVAL 
		
	  , #{nBxh} 
	  ,  #{cDm} 
	  ,  #{cMc} 
	  ,  #{nXsxh} 
	  ,  #{cTj} 
	  , #{cSjdm} 
		  , to_date(#{dDj},'YYYY-MM-DD')
	)

	 
	 SELECT  SEQ_DM.CURRVAL  AS ID FROM dual

mapper.update:
 
			update D_DM 
			 
				 , N_BXH = #{nBxh}
				 , C_DM = #{cDm}
				 , C_MC = #{cMc}
				 , N_XSXH = #{nXsxh}
				 , C_TJ = #{cTj}
				 , C_SJDM = #{cSjdm}
					 , D_DJ = to_date(#{dDj},'YYYY-MM-DD')
			
			  
				  and N_XH = #{nXh}
				  and  N_XH in (${nXh})
