mapper.select:
		 select 
			base.BTN_ID,
			base.BTN_NAME,
			base.BTN_NO,
			base.BTN_CLASS,
			base.BTN_ICON,
			base.BTN_SCRIPT,
			base.MENU_ID,
			base.INITSTATUS
		 from SYS_button base 
  
	  and BTN_ID = #{btnId}
	  and BTN_NAME = #{btnName}
	  and BTN_NO = #{btnNo}
	  and BTN_CLASS = #{btnClass}
	  and BTN_ICON = #{btnIcon}
	  and BTN_SCRIPT, = #{btnScript}
	  and MENU_ID = #{menuId}
	  and INITSTATUS = #{initstatus}

ORDER BY BTN_ID  DESC


 select 
			base.BTN_ID,
			base.BTN_NAME,
			base.BTN_NO,
			base.BTN_CLASS,
			base.BTN_ICON,
			base.BTN_SCRIPT,
			base.MENU_ID,
			base.INITSTATUS
		 from SYS_button base 
  
	  and BTN_ID = #{btnId}
	  and BTN_NAME = #{btnName}
	  and BTN_NO = #{btnNo}
	  and BTN_CLASS = #{btnClass}
	  and BTN_ICON = #{btnIcon}
	  and BTN_SCRIPT, = #{btnScript}
	  and MENU_ID = #{menuId}
	  and INITSTATUS = #{initstatus}

ORDER BY BTN_ID  DESC

				select count(base.USER_ID) from SYS_USER base
 		 
			  and 	USER_ID = #{userId}
			  and 	USER_NAME = #{userName}
			  and 	USER_PASSWORD = #{userName}
			  and 	USER_LOCK = #{userLock}

mapper.insert:
	INSERT INTO SYS_USER(
		 USER_ID
	  , USER_NAME
	  , USER_PASSWORD
	  ,  USER_LOCK
	)VALUES(
		SEQ_SYS_USER_ID.NEXTVAL 
		
	  ,  #{userName}
	  ,  #{userPassword}
	 ,  #{userLock} 
	
	
	)

	 
	 SELECT  SEQ_SYS_USER_ID.CURRVAL  AS ID FROM dual

mapper.update:
 
			update SYS_USER 
			 
				 , USER_NAME = #{userName}
				 , USER_PASSWORD = #{userPassword}
				 , USER_LOCK = #{userLock}
			
			  
				  and USER_ID = #{userId}
				  and  USER_ID in (${userIds})
