mapper.select:
		 select 
			base.MENU_ID,
			base.MENU_NO,
			base.MENU_PARENT_NO,
			base.MENU_NAME,
			base.MENU_URL,
			base.MENU_ICON,
			base.ISVISIBLE
		 from SYS_MENU base 
  
	  and MENU_ID = #{menuId}
	  and MENU_NO = #{menuNo}
	  and MENU_PARENT_NO = #{menuParentNo}
	  and MENU_NAME = #{menuName}
	  and MENU_URL = #{menuUrl}
	  and MENU_ICON = #{menuIcon}
	  and ISVISIBLE = #{isvisible}

ORDER BY MENU_ID  ASC

	
 select 
				base.MENU_ID,
				base.MENU_NO,
				base.MENU_PARENT_NO,
				base.MENU_NAME,
				base.MENU_URL,
				base.MENU_ICON,
				base.ISVISIBLE
