		  and MENU_URL = #{menuUrl}
		  and MENU_ICON = #{menuIcon}
		  and ISVISIBLE = #{isvisible}
	
	ORDER BY MENU_ID  ASC

				select count(base.MENU_ID) from SYS_MENU base
 		  
			  and MENU_ID = #{menuId}
			  and MENU_NO = #{menuNo}
			  and MENU_PARENT_NO = #{menuParentNo}
			  and MENU_NAME = #{menuName}
			  and MENU_URL = #{menuUrl}
			  and MENU_ICON = #{menuIcon}
			  and ISVISIBLE = #{isvisible}

mapper.insert:
	INSERT INTO SYS_MENU(
		 MENU_ID
	  , MENU_NO
	  ,  MENU_PARENT_NO
	  ,  MENU_NAME
	  ,  MENU_URL
	  ,  MENU_ICON
				  , ISVISIBLE
	)VALUES(
		SEQ_SYS_MENU_ID.NEXTVAL 
		
	  , #{menuNo} 
	  ,  #{menuParentNo} 
	  ,  #{menuName} 
	  ,  #{menuUrl} 
	  ,  #{menuIcon} 
	  , #{isvisible} 
	)

	 
	 SELECT  SEQ_SYS_MENU_ID.CURRVAL  AS ID FROM dual

mapper.update:
 
			update SYS_MENU 
			 
				 , MENU_NO = #{menuNo}
				 , MENU_PARENT_NO = #{menuParentNo}
				 , MENU_NAME = #{menuName}
				 , MENU_URL = #{menuUrl}
				 , MENU_ICON = #{menuIcon}
				 , ISVISIBLE = #{isvisible}
			
			  
				  and MENU_ID = #{menuId}
				  and  MENU_ID in (${menuIds})

 
			update SYS_MENU set 
				MENU_ID = #{menuId},
				MENU_NO = #{menuNo},
				MENU_PARENT_NO = #{menuParentNo},
				MENU_NAME = #{menuName}
				MENU_URL = #{menuUrl}
				MENU_ICON = #{menuIcon}
				ISVISIBLE = #{isvisible}
			WHERE 
				MENU_ID = #{menuId}
