mapper.select:
		 select 
			base.USER_ID,
			base.USER_NAME,
			base.USER_PASSWORD,
			base.USER_LOCK,
			case base.USER_LOCK when '1' then '已锁定'  else '未锁定' end as USER_LOCK_NAME,
		 from SYS_USER base 
  
	  and USER_ID = #{userId}
	  and USER_NAME = #{userName}
	  and USER_PASSWORD = #{userPassword}
	  and USER_LOCK = #{userLock}

ORDER BY USER_ID  DESC

select * from ( select base.USER_ID, base.USER_NAME, base.USER_PASSWORD, base.USER_LOCK, case base.USER_LOCK when '1' then '已锁定' else '未锁定' end as USER_LOCK_NAME, role.role_id ROLE_ID, role.role_name ROLE_NAME from SYS_USER base,SYS_GROUP_ROLE gr ,SYS_ROLE role where base.user_id = gr.group_id(+) and role.role_id(+)=gr.role_id
)
and USER_ID = #{userId} and USER_NAME = #{userName} and USER_PASSWORD = #{userName} and USER_LOCK = #{userLock}
ORDER BY USER_ID DESC

				select count(base.USER_ID) from SYS_USER base
 		 
			  and 	USER_ID = #{userId}
			  and 	USER_NAME = #{userName}
			  and 	USER_PASSWORD = #{userName}
			  and 	USER_LOCK = #{userLock}

mapper.insert:
	INSERT INTO SYS_USER(
		 USER_ID
	  , USER_NAME
	  , USER_PASSWORD
	  ,  USER_LOCK
	)VALUES(
		SEQ_SYS_USER_ID.NEXTVAL 
		
	  ,  #{userName}
	  ,  #{userPassword}
	 ,  #{userLock} 
	)

	 
	 SELECT  SEQ_SYS_USER_ID.CURRVAL  AS ID FROM dual

	INSERT INTO SYS_GROUP_ROLE(
		GROUP_ROLE_ID,
		GROUP_ID,
		ROLE_ID
	)VALUES(
		 SEQ_SYS_GROUP_ROLE_ID.NEXTVAL,  
		
		#{userId},
		#{roleId}
	)

	  
	 SELECT  SEQ_SYS_GROUP_ROLE_ID.CURRVAL  AS ID FROM dual

mapper.update:
 
			update SYS_USER 
			 
				 , USER_NAME = #{userName}
				 , USER_PASSWORD = #{userPassword}
				 , USER_LOCK = #{userLock}
			
			  
				  and USER_ID = #{userId}
				  and  USER_ID in (${userIds})
